defmodule Jwt.Display do
  @doc ~S"""
  Displays the content of a token. Useful to inspect them quickly. It does not do any verification on them.

  ## Examples

      iex> Jwt.Display.display("eyJhbGciOiJSUzI1NiIsImtpZCI6IjEwZWZiZjlmOWEzZThlYzVlN2RmYTc5NjFkNzFlMmU0YmZkYTI0MzUifQ..Kc90u_gtZyhq6glw6UoYQSInZx9r16uqrRO7g50x17JWH7VkyAZrh3sfjdBYpGtDNJjDRBKSxuinpDjpyfiCp3-XAqqOUWqziyYvkV4-CdQvNhcnUQFXjjx_CzNiiEi5PRPCHhX4ajidet1NH4Me02S17gwOZiaZfed1BMWQuQ_7Hf2RsX5FID1xqOpcaaouMFcrqQFmdBIbcstHamWxs9D83c4JpOsioNOMb6-LBinzOg7qdxr1D4NvHD6VSXBTbyXiOBjK2elLU1iCz_Hz_BH-R1IYCdTRr5PczRWdSCgoTdZ7ds1nTTglfuXlGNbaEhhzsFxX8OCR4uNK6vbWXQ")
      {:ok, {:claims,
          %{
              "aud" => "347384562113-rdm6sldm1mb38k4umrco3p8l7r5hg0k5.apps.googleusercontent.com",
              "azp" => "347384562113-r28pjd0uc9pof5cm1p0nnl25s97j8uqp.apps.googleusercontent.com",
              "email" => "alejandro.mezcua@byteabyte.net",
              "email_verified" => true,
              "exp" => 1473229464,
              "family_name" => "Mezcua",
              "given_name" => "Alejandro",
              "hd" => "byteabyte.net",
              "iat" => 1473225864,
              "iss" => "https://accounts.google.com",
              "locale" => "en",
              "name" => "Alejandro Mezcua",
              "picture" => "https://lh3.googleusercontent.com/-fIJT7G2uZ3E/AAAAAAAAAAI/AAAAAAAAAQY/JvCVmFHXnr8/s96-c/photo.jpg",
              "sub" => "110361400244871225414"
              }}}

  """
  def display(token) do
    token_parts = String.split(token, ".")

    {:ok,
     _display(
       Enum.map(token_parts, fn part -> Base.url_decode64(part, padding: false) end),
       token_parts
     )}
  end

  defp _display([{:ok, _header}, {:ok, claims}, {:ok, _signature}], [
         _header_b64,
         _claims_b64,
         _signature_b64
       ]) do
    {:claims, Jason.decode!(claims)}
  end
end
